import{_ as o,o as e,c as t,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 13.1-307 Permissible limitation of stock ownership or voting rights.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-307.md","filePath":"vacode/13.1-307.md"}'),i={name:"vacode/13.1-307.md"},s=r('<h1 id="§-13-1-307-permissible-limitation-of-stock-ownership-or-voting-rights" tabindex="-1">§ 13.1-307 Permissible limitation of stock ownership or voting rights. <a class="header-anchor" href="#§-13-1-307-permissible-limitation-of-stock-ownership-or-voting-rights" aria-label="Permalink to &quot;§ 13.1-307 Permissible limitation of stock ownership or voting rights.&quot;">​</a></h1><p>Any cooperative association may, either in its charter or by bylaws, provide and require that no membership or share of its stock shall be issued to or owned by any person not a member of a nonstock corporation or nonstock corporations named or designated in such charter or bylaws, or may in like manner provide that memberships or shares of its stock may be issued to or owned by persons not members of such designated nonstock corporation or nonstock corporations, but that when so owned such stock shall have no voting power. The provisions of this section shall not apply to any worker cooperative established under Article 3 (§ <a href="/vacode/13.1-346/">13.1-346</a> et seq.).</p><p>Code 1950, § 13-244; 1956, c. 428; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0217" target="_blank" rel="noreferrer">217</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0673" target="_blank" rel="noreferrer">673</a>.</p>',3),a=[s];function n(c,h,l,p,d,m){return e(),t("div",null,a)}const b=o(i,[["render",n]]);export{f as __pageData,b as default};
