import{_ as t,o,c as r,z as e,a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 13.1-325 Removal of director.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-325.md","filePath":"vacode/13.1-325.md"}'),i={name:"vacode/13.1-325.md"},s=e("h1",{id:"§-13-1-325-removal-of-director",tabindex:"-1"},[a("§ 13.1-325 Removal of director. "),e("a",{class:"header-anchor",href:"#§-13-1-325-removal-of-director","aria-label":'Permalink to "§ 13.1-325 Removal of director."'},"​")],-1),n=e("p",null,"Any member may ask for the removal of an elected director by filing charges with the secretary or president of the association, together with a petition signed by ten per centum of the members requesting the removal of the director in question. The removal shall be voted upon at the next meeting of the members, and by two-thirds of the voting power voting thereon the association may remove the director. The director whose removal is requested shall be served with a copy of the charges not less than ten days prior to the meeting and shall have an opportunity at the meeting to be heard in person and by counsel and to present evidence; and the persons requesting the removal shall have the same opportunity. In case the bylaws provide for election of directors by districts, then the petition for removal of a director must be signed by twenty per centum of the members residing in the district from which he was elected. The board must call a special meeting of the members residing in the district to consider the removal of the director; and by two-thirds of the voting power of the members of that district voting thereon the director in question shall be removed from office.",-1),h=e("p",null,"Code 1950, § 13-268; 1956, c. 428.",-1),d=[s,n,h];function c(l,m,f,p,v,b){return o(),r("div",null,d)}const g=t(i,[["render",c]]);export{u as __pageData,g as default};
