import{_ as o,o as t,c as a,z as e,a as s}from"./chunks/framework.9482e208.js";const w=JSON.parse('{"title":"§ 13.1-340 Sale, mortgage or other disposition of assets.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-340.md","filePath":"vacode/13.1-340.md"}'),r={name:"vacode/13.1-340.md"},i=e("h1",{id:"§-13-1-340-sale-mortgage-or-other-disposition-of-assets",tabindex:"-1"},[s("§ 13.1-340 Sale, mortgage or other disposition of assets. "),e("a",{class:"header-anchor",href:"#§-13-1-340-sale-mortgage-or-other-disposition-of-assets","aria-label":'Permalink to "§ 13.1-340 Sale, mortgage or other disposition of assets."'},"​")],-1),n=e("p",null,"The sale, lease, exchange, mortgage, pledge or other disposition of all, or substantially all, the property and assets of an association, when made in the usual and regular course of the business of the association, may be made upon such terms and conditions and for such consideration, which may consist in whole or in part of money or property, real or personal, including shares of any other corporation, domestic or foreign, as shall be authorized by its board of directors; and in such case no authorization or consent of the members shall be required.",-1),h=e("p",null,"Unless otherwise provided in the articles of incorporation, a mortgage or pledge of all or any part of the property and assets, with or without the goodwill, of an association, though not made in usual and regular course of its business, may be made for money upon such terms and conditions as shall be authorized by its board of directors and no authorization or consent of members shall be required.",-1),l=e("p",null,"A sale, lease or exchange, or a mortgage or pledge for a consideration other than money, of all, or substantially all, the property and assets, with or without the goodwill, of an association, if not made in the usual and regular course of its business, may be made upon such terms and conditions and for such consideration, which may consist in whole or in part of money or property, real or personal, including shares of any other corporation, domestic or foreign, as may be authorized in the following manner:",-1),d=e("p",null,"The board of directors shall adopt a resolution recommending such sale, lease, exchange, mortgage, pledge or other disposition and directing that it be submitted to a vote at a meeting of members having voting rights, which may be either an annual or a special meeting. Written notice stating that the purpose, or one of the purposes, of such meeting is to consider the sale, lease, exchange, mortgage, pledge or other disposition of all, or substantially all, the property and assets of the association shall be given to each member entitled to vote at such meeting, at least ten days prior to such meeting. At such meeting the members may authorize such sale, lease, exchange, mortgage, pledge or other disposition and may fix, or may authorize the board of directors to fix, any or all of the terms and conditions thereof and the consideration to be received by the association therefor. Such authorization shall require the vote of at least two-thirds of the votes entitled to be cast by members present or represented by proxy at such meeting. After such authorization by a vote of members, the board of directors, nevertheless in its discretion, may abandon such sale, lease, exchange, mortgage, pledge or other disposition of assets, subject to the rights of third parties under any contracts relating thereto, without further action or approval by members.",-1),c=e("p",null,"1956, c. 428.",-1),m=[i,n,h,l,d,c];function u(p,g,f,b,y,_){return t(),a("div",null,m)}const x=o(r,[["render",u]]);export{w as __pageData,x as default};
