import{_ as e,o,c as r,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 13.1-347 Formation of worker cooperative; purpose.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-347.md","filePath":"vacode/13.1-347.md"}'),t={name:"vacode/13.1-347.md"},i=a('<h1 id="§-13-1-347-formation-of-worker-cooperative-purpose" tabindex="-1">§ 13.1-347 Formation of worker cooperative; purpose. <a class="header-anchor" href="#§-13-1-347-formation-of-worker-cooperative-purpose" aria-label="Permalink to &quot;§ 13.1-347 Formation of worker cooperative; purpose.&quot;">​</a></h1><p>A. Any corporation incorporated under Article 3 (§ <a href="/vacode/13.1-618/">13.1-618</a> et seq.) of Chapter 9 may elect to be governed as a worker cooperative in accordance with the provisions of this article by so stating in its articles of incorporation or articles of amendment filed in accordance with § <a href="/vacode/13.1-710/">13.1-710</a>. The offering of an employee stock ownership plan governed by 26 U.S.C. § 401 by a corporation incorporated under Article 3 (§ <a href="/vacode/13.1-618/">13.1-618</a> et seq.) of Chapter 9 to its employees shall not be considered an election to be governed as a worker cooperative.</p><p>B. A worker cooperative may be formed for any lawful purpose, provided that it shall be organized and shall conduct its business primarily for the mutual benefit of its members.</p><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0673" target="_blank" rel="noreferrer">673</a>.</p>',4),n=[i];function c(s,p,d,l,f,_){return o(),r("div",null,n)}const v=e(t,[["render",c]]);export{m as __pageData,v as default};
