import{_ as e,o as r,c as o,O as n}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 13.1-566 Service of process on nonresident franchisor.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-566.md","filePath":"vacode/13.1-566.md"}'),a={name:"vacode/13.1-566.md"},i=n('<h1 id="§-13-1-566-service-of-process-on-nonresident-franchisor" tabindex="-1">§ 13.1-566 Service of process on nonresident franchisor. <a class="header-anchor" href="#§-13-1-566-service-of-process-on-nonresident-franchisor" aria-label="Permalink to &quot;§ 13.1-566 Service of process on nonresident franchisor.&quot;">​</a></h1><p>Every nonresident franchisor who has a franchise registered hereunder and which is not a corporation complying with § <a href="http://law.lis.virginia.gov/vacode/13.1-759/" target="_blank" rel="noreferrer">13.1-759</a> or § <a href="http://law.lis.virginia.gov/vacode/13.1-767/" target="_blank" rel="noreferrer">13.1-767</a> shall appoint in writing the clerk of the Commission as his agent upon whom may be served any process, notice, order or demand. Every nonresident franchisor who sells or offers to sell a franchise registered hereunder and every nonresident franchisor whose franchise is sold or offered to be sold in this Commonwealth shall be deemed to have appointed the clerk of the Commission as his agent upon whom may be served, in any matter arising under this chapter any process, notice, order or demand. Service may be made on the clerk in accordance with § <a href="http://law.lis.virginia.gov/vacode/12.1-19.1/" target="_blank" rel="noreferrer">12.1-19.1</a>.</p><p>1972, c. 561; 1978, c. 670; 1990, c. 263; 1991, c. 672; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0148" target="_blank" rel="noreferrer">148</a>.</p>',3),s=[i];function t(c,h,d,l,f,p){return r(),o("div",null,s)}const m=e(a,[["render",t]]);export{v as __pageData,m as default};
