import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 13.1-571 Civil remedies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-571.md","filePath":"vacode/13.1-571.md"}'),o={name:"vacode/13.1-571.md"},r=t('<h1 id="§-13-1-571-civil-remedies" tabindex="-1">§ 13.1-571 Civil remedies. <a class="header-anchor" href="#§-13-1-571-civil-remedies" aria-label="Permalink to &quot;§ 13.1-571 Civil remedies.&quot;">​</a></h1><p>(a) Any franchisee who has declared the franchise void under § <a href="http://law.lis.virginia.gov/vacode/13.1-565/" target="_blank" rel="noreferrer">13.1-565</a> or who has suffered damages by reason of any violation of § <a href="http://law.lis.virginia.gov/vacode/13.1-564/" target="_blank" rel="noreferrer">13.1-564</a> may bring an action against its franchisor to recover the damages sustained by reason thereof. Such franchisee, if successful, shall also be entitled to the costs of the action, including reasonable attorney&#39;s fees.</p><p>(b) No suit shall be maintained to enforce any liability created under this section unless brought within four years after the cause of action upon which it is based arose.</p><p>(c) Any condition, stipulation or provision binding any person to waive compliance with any provision of this chapter or of any rule or order thereunder shall be void; provided, however, that nothing contained herein shall bar the right of a franchisor and franchisee to agree to binding arbitration of disputes consistent with the provisions of this chapter.</p><p>(d) The rights and remedies provided by this section shall be in addition to any and all other rights and remedies that may exist at law or in equity.</p><p>1972, c. 561.</p>',6),n=[r];function s(h,d,c,l,f,p){return a(),i("div",null,n)}const u=e(o,[["render",s]]);export{v as __pageData,u as default};
