import{_ as i,o,c as e,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 13.1-692.1 Limitation on liability of officers and directors; exception.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-692.1.md","filePath":"vacode/13.1-692.1.md"}'),r={name:"vacode/13.1-692.1.md"},a=t('<h1 id="§-13-1-692-1-limitation-on-liability-of-officers-and-directors-exception" tabindex="-1">§ 13.1-692.1 Limitation on liability of officers and directors; exception. <a class="header-anchor" href="#§-13-1-692-1-limitation-on-liability-of-officers-and-directors-exception" aria-label="Permalink to &quot;§ 13.1-692.1 Limitation on liability of officers and directors; exception.&quot;">​</a></h1><p>A. In any proceeding brought by or in the right of a corporation or brought by or on behalf of shareholders of the corporation, the damages assessed against an officer or director arising out of a single transaction, occurrence or course of conduct shall not exceed the lesser of:</p><ol><li><p>The monetary amount, including the elimination of liability, specified in the articles of incorporation or, if approved by the shareholders, in the bylaws as a limitation on or elimination of the liability of the officer or director; or</p></li><li><p>The greater of (i) $100,000 or (ii) the amount of cash compensation received by the officer or director from the corporation during the 12 months immediately preceding the act or omission for which liability was imposed.</p></li></ol><p>B. The liability of an officer or director shall not be limited as provided in this section if the officer or director engaged in willful misconduct or a knowing violation of the criminal law or of any federal or state securities law, including, without limitation, any claim of unlawful insider trading or manipulation of the market for any security.</p><p>C. No limitation or elimination of liability adopted pursuant to this section may be affected by any amendment of the articles of incorporation or bylaws with respect to any action or omission occurring before such amendment.</p><p>1987, cc. 59, 257; 1988, c. 561; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0734" target="_blank" rel="noreferrer">734</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1226" target="_blank" rel="noreferrer">1226</a>.</p>',6),n=[a];function c(l,s,f,d,h,p){return o(),e("div",null,n)}const _=i(r,[["render",c]]);export{u as __pageData,_ as default};
