import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 13.1-693 Required officers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-693.md","filePath":"vacode/13.1-693.md"}'),o={name:"vacode/13.1-693.md"},i=t('<h1 id="§-13-1-693-required-officers" tabindex="-1">§ 13.1-693 Required officers. <a class="header-anchor" href="#§-13-1-693-required-officers" aria-label="Permalink to &quot;§ 13.1-693 Required officers.&quot;">​</a></h1><p>A. Except as provided in an agreement authorized by § <a href="/vacode/13.1-671.1/">13.1-671.1</a>, a corporation shall have such officers with such titles and duties as shall be described in the bylaws or in a resolution of the board of directors that is in accordance with the bylaws and as may be necessary to enable it to execute documents that comply with subsection F of § <a href="/vacode/13.1-604/">13.1-604</a>.</p><p>B. Officers shall be elected by the board of directors, except that an officer may appoint one or more officers or assistant officers if authorized by the bylaws or the board of directors.</p><p>C. The secretary of the corporation shall have the responsibility for preparing the minutes of the directors&#39; and shareholders&#39; meetings and for maintaining and authenticating the records of the corporation required to be kept under subsection E of § <a href="/vacode/13.1-770/">13.1-770</a>.</p><p>D. The same individual may simultaneously hold more than one office in a corporation.</p><p>E. Election or appointment of an officer does not of itself create any contract rights in the officer or the corporation.</p><p>Code 1950, § 13.1-45; 1956, c. 428; 1972, c. 606; 1975, c. 500; 1982, c. 372; 1985, c. 522; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0189" target="_blank" rel="noreferrer">189</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0765" target="_blank" rel="noreferrer">765</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0734" target="_blank" rel="noreferrer">734</a>.</p>',7),n=[i];function c(s,f,d,h,l,p){return r(),a("div",null,n)}const b=e(o,[["render",c]]);export{_ as __pageData,b as default};
