import{_ as e,o as a,c as i,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 13.1-702 Advance for expenses and indemnification for officers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-702.md","filePath":"vacode/13.1-702.md"}'),o={name:"vacode/13.1-702.md"},r=n('<h1 id="§-13-1-702-advance-for-expenses-and-indemnification-for-officers" tabindex="-1">§ 13.1-702 Advance for expenses and indemnification for officers. <a class="header-anchor" href="#§-13-1-702-advance-for-expenses-and-indemnification-for-officers" aria-label="Permalink to &quot;§ 13.1-702 Advance for expenses and indemnification for officers.&quot;">​</a></h1><p>Unless limited by a corporation&#39;s articles of incorporation:</p><ol><li><p>An officer of the corporation who is a party to a proceeding because he is an officer is entitled to mandatory indemnification under § <a href="/vacode/13.1-698/">13.1-698</a> and is entitled to apply for court-ordered advance or reimbursement of expenses and indemnification under § <a href="/vacode/13.1-700.1/">13.1-700.1</a>, in each case to the same extent as a director; and</p></li><li><p>The corporation may indemnify and advance expenses under this article to an officer who is a party to a proceeding because the individual is an officer to the same extent as to a director.</p></li></ol><p>Code 1950, § 13.1-3.1; 1968, c. 570; 1975, c. 500; 1979, c. 99; 1985, c. 522; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0765" target="_blank" rel="noreferrer">765</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0734" target="_blank" rel="noreferrer">734</a>.</p>',4),t=[r];function c(s,d,f,p,l,_){return a(),i("div",null,t)}const v=e(o,[["render",c]]);export{m as __pageData,v as default};
