import{_ as e,o,c as r,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 13.1-706 Amendment of articles of incorporation by the board of directors.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-706.md","filePath":"vacode/13.1-706.md"}'),t={name:"vacode/13.1-706.md"},i=a('<h1 id="§-13-1-706-amendment-of-articles-of-incorporation-by-the-board-of-directors" tabindex="-1">§ 13.1-706 Amendment of articles of incorporation by the board of directors. <a class="header-anchor" href="#§-13-1-706-amendment-of-articles-of-incorporation-by-the-board-of-directors" aria-label="Permalink to &quot;§ 13.1-706 Amendment of articles of incorporation by the board of directors.&quot;">​</a></h1><p>A. Where no shares of the corporation are issued and outstanding, a corporation&#39;s board of directors may adopt an amendment of the corporation&#39;s articles of incorporation without shareholder approval.</p><p>B. Unless the articles of incorporation provide otherwise, a corporation&#39;s board of directors may adopt amendments of the corporation&#39;s articles of incorporation without shareholder approval:</p><ol><li><p>To delete the names and addresses of the initial directors;</p></li><li><p>To delete the name of the initial registered agent or the address of the initial registered office, if a statement of change described in § <a href="/vacode/13.1-635/">13.1-635</a> is on file with the Commission;</p></li><li><p>If the corporation has only one class of shares outstanding:</p></li></ol><p>a. To change each issued and unissued authorized share of the class into a greater number of whole shares of that class; or</p><p>b. To increase the number of authorized shares of the class to the extent necessary to permit the issuance of shares as a share dividend;</p><ol start="4"><li><p>To eliminate or change the par value of the shares of any class or series;</p></li><li><p>To change the corporate name;</p></li><li><p>If the corporation has or will become a holding company under § <a href="/vacode/13.1-719.1/">13.1-719.1</a>, to change the corporate name to the former name of the constituent corporation;</p></li><li><p>If the corporation is registered as an open-end management investment company under the federal Investment Company Act of 1940, to increase or decrease the aggregate number of shares or the number of shares of any class or series within any class that the corporation is authorized to issue;</p></li><li><p>To delete a class or series of shares from the articles of incorporation when there are no shares of the class or series, including any rights to any such shares, outstanding; or</p></li><li><p>To make any other change expressly permitted by this chapter to be made without shareholder approval.</p></li></ol><p>1985, c. 522; 1989, c. 483; 1990, c. 292; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0765" target="_blank" rel="noreferrer">765</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0330" target="_blank" rel="noreferrer">330</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0623" target="_blank" rel="noreferrer">623</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0734" target="_blank" rel="noreferrer">734</a>.</p>',8),n=[i];function s(c,h,l,p,d,f){return o(),r("div",null,n)}const u=e(t,[["render",s]]);export{g as __pageData,u as default};
