import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 13.1-728.9 Nonexclusivity.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-728.9.md","filePath":"vacode/13.1-728.9.md"}'),o={name:"vacode/13.1-728.9.md"},r=i('<h1 id="§-13-1-728-9-nonexclusivity" tabindex="-1">§ 13.1-728.9 Nonexclusivity. <a class="header-anchor" href="#§-13-1-728-9-nonexclusivity" aria-label="Permalink to &quot;§ 13.1-728.9 Nonexclusivity.&quot;">​</a></h1><p>Except as expressly provided in this article, neither the provisions of this article nor their application to any acquiring person shall limit actions that may be taken, or require the taking of any action, by the board of directors or shareholders with respect to any potential changes in control of any public corporation. Regardless of the applicability of this article, in the case of any action taken or not taken by directors, the provisions of § <a href="/vacode/13.1-690/">13.1-690</a> shall apply, and, in determining the best interests of the corporation, a director may consider the possibility that those interests may best be served by the continued independence of the corporation.</p><p>1989, c. 14; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0765" target="_blank" rel="noreferrer">765</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0734" target="_blank" rel="noreferrer">734</a>.</p>',3),n=[r];function s(c,l,h,p,d,_){return t(),a("div",null,n)}const v=e(o,[["render",s]]);export{y as __pageData,v as default};
