import{_ as e,o as a,c as r,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 13.1-746.3 Director duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-746.3.md","filePath":"vacode/13.1-746.3.md"}'),t={name:"vacode/13.1-746.3.md"},i=o('<h1 id="§-13-1-746-3-director-duties" tabindex="-1">§ 13.1-746.3 Director duties. <a class="header-anchor" href="#§-13-1-746-3-director-duties" aria-label="Permalink to &quot;§ 13.1-746.3 Director duties.&quot;">​</a></h1><p>A. Directors shall cause the dissolved corporation to apply its remaining assets to discharge or make reasonable provision for the payment of claims and make distributions in liquidation of assets to shareholders after payment or provision for claims.</p><p>B. Directors of a dissolved corporation that has disposed of claims under § <a href="/vacode/13.1-746/">13.1-746</a>, <a href="/vacode/13.1-746.1/">13.1-746.1</a> or <a href="/vacode/13.1-746.2/">13.1-746.2</a> shall not be liable for breach of subsection A with respect to claims against the dissolved corporation that are barred or satisfied under § <a href="/vacode/13.1-746/">13.1-746</a>, <a href="/vacode/13.1-746.1/">13.1-746.1</a> or <a href="/vacode/13.1-746.2/">13.1-746.2</a>.</p><p>2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0765" target="_blank" rel="noreferrer">765</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0734" target="_blank" rel="noreferrer">734</a>.</p>',4),s=[i];function c(d,n,l,h,p,_){return a(),r("div",null,s)}const v=e(t,[["render",c]]);export{u as __pageData,v as default};
