import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 13.1-815.1 Charter and entrance fees for corporations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-815.1.md","filePath":"vacode/13.1-815.1.md"}'),o={name:"vacode/13.1-815.1.md"},n=r('<h1 id="§-13-1-815-1-charter-and-entrance-fees-for-corporations" tabindex="-1">§ 13.1-815.1 Charter and entrance fees for corporations. <a class="header-anchor" href="#§-13-1-815-1-charter-and-entrance-fees-for-corporations" aria-label="Permalink to &quot;§ 13.1-815.1 Charter and entrance fees for corporations.&quot;">​</a></h1><p>A. Every domestic corporation, upon the granting of its charter or upon domestication, shall pay a charter fee in the amount of $50 into the state treasury, and every foreign corporation shall pay an entrance fee of $50 into the state treasury for its certificate of authority to transact business in the Commonwealth.</p><p>B. For any foreign corporation that files articles of domestication and that had authority to transact business in the Commonwealth at the time of such filing, the charter fee to be charged upon domestication shall be an amount equal to the difference between the amount that would be required by this section and the amount already paid as an entrance fee by such corporation.</p><p>C. For any domestic stock corporation that files articles of conversion to become a domestic corporation, the charter fee to be charged shall be an amount equal to the difference between the amount already paid as a charter fee by the domestic stock corporation and the amount that would be required by this section to be paid.</p><p>1988, c. 405; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0374" target="_blank" rel="noreferrer">374</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0810" target="_blank" rel="noreferrer">810</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0925" target="_blank" rel="noreferrer">925</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0509" target="_blank" rel="noreferrer">509</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0487" target="_blank" rel="noreferrer">487</a>.</p>',5),i=[n];function c(s,h,l,f,p,d){return t(),a("div",null,i)}const g=e(o,[["render",c]]);export{_ as __pageData,g as default};
