import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 13.1-838 Annual meeting.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-838.md","filePath":"vacode/13.1-838.md"}'),o={name:"vacode/13.1-838.md"},i=n('<h1 id="§-13-1-838-annual-meeting" tabindex="-1">§ 13.1-838 Annual meeting. <a class="header-anchor" href="#§-13-1-838-annual-meeting" aria-label="Permalink to &quot;§ 13.1-838 Annual meeting.&quot;">​</a></h1><p>A. A corporation shall hold a meeting of members annually at a time stated in or fixed in accordance with the bylaws.</p><p>B. Except as otherwise determined by the board of directors acting pursuant to subsection C of § <a href="/vacode/13.1-844.2/">13.1-844.2</a>, meetings of members may be held at such place, in or out of the Commonwealth, as may be provided in the bylaws or, where not inconsistent with the bylaws, in the notice of the meeting.</p><p>C. The failure to hold an annual meeting at the time stated in or fixed in accordance with a corporation&#39;s bylaws does not affect the validity of any corporate action.</p><p>Code 1950, § 13.1-213; 1956, c. 428; 1975, c. 500; 1985, c. 522; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0925" target="_blank" rel="noreferrer">925</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0706" target="_blank" rel="noreferrer">706</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0265" target="_blank" rel="noreferrer">265</a>.</p>',5),r=[i];function c(l,s,h,d,p,_){return a(),t("div",null,r)}const g=e(o,[["render",c]]);export{m as __pageData,g as default};
