import{_ as e,o,c as t,O as r}from"./chunks/framework.9482e208.js";const b=JSON.parse(`{"title":"§ 13.1-866 Notice of board of directors' meetings.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-866.md","filePath":"vacode/13.1-866.md"}`),i={name:"vacode/13.1-866.md"},a=r('<h1 id="§-13-1-866-notice-of-board-of-directors-meetings" tabindex="-1">§ 13.1-866 Notice of board of directors&#39; meetings. <a class="header-anchor" href="#§-13-1-866-notice-of-board-of-directors-meetings" aria-label="Permalink to &quot;§ 13.1-866 Notice of board of directors&#39; meetings.&quot;">​</a></h1><p>A. Unless the articles of incorporation or bylaws provide otherwise, regular meetings of the board of directors may be held without notice of the date, time, place, or purpose of the meeting.</p><p>B. Special meetings of the board of directors shall be held upon such notice as is prescribed in the articles of incorporation or bylaws, or when not inconsistent with the articles of incorporation or bylaws, by resolution of the board of directors. The notice need not describe the purpose of the special meeting unless required by the articles of incorporation or bylaws.</p><p>1985, c. 522; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0285" target="_blank" rel="noreferrer">285</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0925" target="_blank" rel="noreferrer">925</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0171" target="_blank" rel="noreferrer">171</a>.</p>',4),n=[a];function s(c,l,d,f,h,p){return o(),t("div",null,n)}const g=e(i,[["render",s]]);export{b as __pageData,g as default};
