import{_ as e,o as r,c as o,O as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 13.1-893 Bylaw provisions increasing quorum or voting requirements for directors.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-893.md","filePath":"vacode/13.1-893.md"}'),i={name:"vacode/13.1-893.md"},a=t('<h1 id="§-13-1-893-bylaw-provisions-increasing-quorum-or-voting-requirements-for-directors" tabindex="-1">§ 13.1-893 Bylaw provisions increasing quorum or voting requirements for directors. <a class="header-anchor" href="#§-13-1-893-bylaw-provisions-increasing-quorum-or-voting-requirements-for-directors" aria-label="Permalink to &quot;§ 13.1-893 Bylaw provisions increasing quorum or voting requirements for directors.&quot;">​</a></h1><p>A. A bylaw that increases a quorum or voting requirement for the board of directors may be amended or repealed:</p><ol><li><p>If originally adopted by the members, only by the members, unless the bylaws otherwise provide; or</p></li><li><p>If adopted by the board of directors, either by the members or by the board of directors.</p></li></ol><p>B. A bylaw adopted or amended by the members that increases a quorum or voting requirement for the board of directors may provide that it shall be amended or repealed only by a specified vote of either the members or the board of directors.</p><p>C. Action by the board of directors under subsection A to amend or repeal a bylaw that changes the quorum or voting requirement applicable to meetings of the board of directors shall be effective only if it meets the quorum requirement and is adopted by the vote required to take action under the quorum and voting requirement then in effect.</p><p>1985, c. 522; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0925" target="_blank" rel="noreferrer">925</a>.</p>',6),n=[a];function s(d,c,l,m,u,p){return r(),o("div",null,n)}const f=e(i,[["render",s]]);export{b as __pageData,f as default};
