import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 13.1-898.7 Abandonment of domestication.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-898.7.md","filePath":"vacode/13.1-898.7.md"}'),a={name:"vacode/13.1-898.7.md"},n=i('<h1 id="§-13-1-898-7-abandonment-of-domestication" tabindex="-1">§ 13.1-898.7 Abandonment of domestication. <a class="header-anchor" href="#§-13-1-898-7-abandonment-of-domestication" aria-label="Permalink to &quot;§ 13.1-898.7 Abandonment of domestication.&quot;">​</a></h1><p>A. Unless otherwise provided in the plan of domestication, after a plan of domestication has been adopted and approved by a domestic corporation as required by this article, and at any time before the certificate of incorporation surrender has become effective, the plan may be abandoned by the corporation without action by its members in accordance with any procedures set forth in the plan or, if no such procedures are set forth in the plan, in the manner determined by the board of directors.</p><p>B. A domesticating corporation that is a foreign corporation may abandon its domestication to a domestic corporation in the manner prescribed by its organic law.</p><p>C. If a domestication is abandoned after articles of incorporation surrender or articles of domestication have been filed with the Commission but before the certificate of incorporation surrender or certificate of domestication has become effective, a statement of abandonment signed by the domesticating corporation shall be delivered to the Commission for filing prior to the effective time and date of the certificate of incorporation surrender or certificate of domestication. If the Commission finds that the statement of abandonment complies with the requirements of law, it shall issue a certificate of abandonment, effective as of the date and time the statement of abandonment was received by the Commission, and the domestication shall be deemed abandoned and shall not become effective.</p><p>D. The statement of abandonment shall contain:</p><ol><li><p>The name of the domesticating corporation and its jurisdiction of formation;</p></li><li><p>When the domestication corporation is a foreign corporation, the name of the domesticated corporation set forth in the articles of domestication;</p></li><li><p>The date on which the articles of incorporation surrender or articles of domestication were filed with the Commission;</p></li><li><p>The date and time on which the Commission&#39;s certificate of incorporation surrender or certificate of domestication becomes effective; and</p></li><li><p>A statement that domestication is being abandoned in accordance with this section or, when the domesticating corporation is a foreign corporation, a statement that the foreign corporation abandoned the domestication as required by its organic law.</p></li></ol><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0374" target="_blank" rel="noreferrer">374</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0925" target="_blank" rel="noreferrer">925</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0623" target="_blank" rel="noreferrer">623</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0487" target="_blank" rel="noreferrer">487</a>.</p>',7),r=[n];function c(s,d,f,h,m,l){return t(),o("div",null,r)}const g=e(a,[["render",c]]);export{b as __pageData,g as default};
