import{_ as e,o,c as t,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 13.1-934 Scope of inspection right.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-934.md","filePath":"vacode/13.1-934.md"}'),a={name:"vacode/13.1-934.md"},i=r('<h1 id="§-13-1-934-scope-of-inspection-right" tabindex="-1">§ 13.1-934 Scope of inspection right. <a class="header-anchor" href="#§-13-1-934-scope-of-inspection-right" aria-label="Permalink to &quot;§ 13.1-934 Scope of inspection right.&quot;">​</a></h1><p>A. A member&#39;s agent or attorney has the same inspection and copying rights as the member he represents.</p><p>B. The right to copy records under § <a href="http://law.lis.virginia.gov/vacode/13.1-933/" target="_blank" rel="noreferrer">13.1-933</a> includes, if reasonable, the right to receive copies by xerographic or other means, including copies through an electronic transmission if available and so requested by the member.</p><p>C. The corporation may impose a reasonable charge, covering the costs of labor and material, for copies of any documents provided to the member. The charge may not exceed the estimated cost of production, reproduction, and transmission of the records.</p><p>D. The corporation may comply with a member&#39;s demand to inspect the record of members under subdivision B 3 of § <a href="http://law.lis.virginia.gov/vacode/13.1-933/" target="_blank" rel="noreferrer">13.1-933</a> by providing the member with a list of its members that was compiled no earlier than the date of the member&#39;s demand.</p><p>1985, c. 522; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0925" target="_blank" rel="noreferrer">925</a>.</p>',6),n=[i];function s(c,h,p,d,m,l){return o(),t("div",null,n)}const g=e(a,[["render",s]]);export{_ as __pageData,g as default};
