import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 15.2-1215 Authority to cut growth of grass or lawn area in counties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-1215.md","filePath":"vacode/15.2-1215.md"}'),o={name:"vacode/15.2-1215.md"},n=t('<h1 id="§-15-2-1215-authority-to-cut-growth-of-grass-or-lawn-area-in-counties" tabindex="-1">§ 15.2-1215 Authority to cut growth of grass or lawn area in counties. <a class="header-anchor" href="#§-15-2-1215-authority-to-cut-growth-of-grass-or-lawn-area-in-counties" aria-label="Permalink to &quot;§ 15.2-1215 Authority to cut growth of grass or lawn area in counties.&quot;">​</a></h1><p>A. Any county may by ordinance require that the owner of occupied residential real property therein cut the grass or lawn area of less than one-half acre on such property or any part thereof at such time or times as the governing body shall prescribe when growth on such grass or lawn area exceeds 12 inches in height; or may whenever the governing body deems it necessary, after reasonable notice, have such grass or lawn area cut by its agents or employees, in which event, the cost and expenses thereof shall be chargeable to and paid by the owner of such property and may be collected by the county as taxes and levies are collected. No such ordinance adopted by the county shall have any force and effect within the corporate limits of any town. Violation of such ordinance may be punishable by a civil penalty not to exceed $100.</p><p>B. No such ordinance shall be applicable to land zoned for or in active farming operation.</p><p>1988, c. 625, § 15.1-11.01; 1990, c. 155; 1991, cc. 314, 315; 1997, cc. 587, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0640" target="_blank" rel="noreferrer">640</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0756" target="_blank" rel="noreferrer">756</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0309" target="_blank" rel="noreferrer">309</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0700" target="_blank" rel="noreferrer">700</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0252" target="_blank" rel="noreferrer">252</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0396" target="_blank" rel="noreferrer">396</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0385" target="_blank" rel="noreferrer">385</a>.</p>',4),i=[n];function c(l,s,h,g,f,p){return r(),a("div",null,i)}const _=e(o,[["render",c]]);export{d as __pageData,_ as default};
