import{_ as t,o as a,c as o,z as e,a as n}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 15.2-1229 Petty cash funds.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-1229.md","filePath":"vacode/15.2-1229.md"}'),s={name:"vacode/15.2-1229.md"},r=e("h1",{id:"§-15-2-1229-petty-cash-funds",tabindex:"-1"},[n("§ 15.2-1229 Petty cash funds. "),e("a",{class:"header-anchor",href:"#§-15-2-1229-petty-cash-funds","aria-label":'Permalink to "§ 15.2-1229 Petty cash funds."'},"​")],-1),c=e("p",null,"Whenever the governing body of any county determines that more efficient administration would be promoted thereby, it may by resolution establish one or more petty cash funds not exceeding $5,000 each for the payment of claims arising from commitments made pursuant to law. Any person into whose hands any such fund is placed may pay such claims therefrom, without necessity of prior receipt and audit of the claims by the governing body and without approval and issuance of the warrant of the governing body or the county treasurer. Such person shall render an account of the same and make a settlement thereof annually in form and manner prescribed by the Auditor of Public Accounts. Such person shall give bond with surety in the amount of $10,000; however, additional bond shall not be required of any person already bonded in the required amount.",-1),d=e("p",null,"Code 1950, § 15-254; 1962, cc. 399, 623, § 15.1-548; 1977, c. 227; 1983, c. 101; 1997, c. 587.",-1),i=[r,c,d];function h(u,l,m,f,p,y){return a(),o("div",null,i)}const v=t(s,[["render",h]]);export{b as __pageData,v as default};
