import{_ as o,o as t,c as n,z as e,a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 15.2-1246 Appeal from disallowance of claim.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-1246.md","filePath":"vacode/15.2-1246.md"}'),i={name:"vacode/15.2-1246.md"},l=e("h1",{id:"§-15-2-1246-appeal-from-disallowance-of-claim",tabindex:"-1"},[a("§ 15.2-1246 Appeal from disallowance of claim. "),e("a",{class:"header-anchor",href:"#§-15-2-1246-appeal-from-disallowance-of-claim","aria-label":'Permalink to "§ 15.2-1246 Appeal from disallowance of claim."'},"​")],-1),r=e("p",null,"When a claim of any person against a county is disallowed in whole or in part by the governing body, if such person is present, he may appeal from the decision of the governing body within 30 days from the date of the decision. If the claimant is not present, the clerk of the governing body shall serve a written notice of the disallowance on him or his agent, and he may appeal from the decision within 30 days after service of such notice. In no case shall the appeal be taken after the lapse of six months from the date of the decision. The appeal shall be filed with the circuit court for the county. No appeal shall be allowed unless the amount disallowed exceeds $10. The disallowance may be appealed by serving written notice on the clerk of the governing body and executing a cash or surety bond or irrevocable letter of credit to the county in the amount of $250, with condition for the faithful prosecution of such appeal, and the payment of all costs imposed on the appellant by the court.",-1),s=e("p",null,[a("Code 1950, § 15-259; 1962, c. 623, § 15.1-552; 1983, c. 114; 1997, c. 587; 2000, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0300",target:"_blank",rel:"noreferrer"},"300"),a("; 2010, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0668",target:"_blank",rel:"noreferrer"},"668"),a(".")],-1),c=[l,r,s];