import{_ as i,o as t,c as a,z as e,a as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 15.2-1300.1 Joint aid agreements by localities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-1300.1.md","filePath":"vacode/15.2-1300.1.md"}'),r={name:"vacode/15.2-1300.1.md"},n=e("h1",{id:"§-15-2-1300-1-joint-aid-agreements-by-localities",tabindex:"-1"},[o("§ 15.2-1300.1 Joint aid agreements by localities. "),e("a",{class:"header-anchor",href:"#§-15-2-1300-1-joint-aid-agreements-by-localities","aria-label":'Permalink to "§ 15.2-1300.1 Joint aid agreements by localities."'},"​")],-1),s=e("p",null,[o("A locality may, by ordinance or resolution, authorize its chief administrative officer to arrange for provision of aid to other localities or receipt of aid from other localities in situations where a locality does not declare a local emergency, including approval by the chief administrator of agreements with other localities, subject to availability of resources. In situations where localities declare a local emergency, the provision or receipt of aid may occur pursuant to § "),e("a",{href:"http://law.lis.virginia.gov/vacode/44-146.20/",target:"_blank",rel:"noreferrer"},"44-146.20"),o(". The ordinance or resolution may include terms and conditions deemed necessary by the governing body for participation in such aid and shall set forth the scope of the chief administrator's authority, including the type of aid that may be provided or received, or may generally authorize participation in provision or receipt of any type of aid including personnel, equipment, or other resources for public purposes. Prior to providing or receiving aid, the chief administrator shall conduct an assessment of available resources and shall consider establishing terms for the supervision of personnel, the term of deployment, payment or reimbursement of costs, and verification of insurance coverage.")],-1),l=e("p",null,"The ordinance or resolution may allow for the participation of volunteers and, with approval of the constitutional officer, constitutional officers and their staffs. Deployed personnel acting pursuant to the ordinance or resolution of the governing body shall have the same authority and immunity in other localities as in the locality where they are employed or volunteer.",-1),c=e("p",null,[o("2011, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0267",target:"_blank",rel:"noreferrer"},"267"),o(".")],-1),d=[n,s,l,c];function h(p,f,u,m,y,g){return t(),a("div",null,d)}const b=i(r,[["render",h]]);export{_ as __pageData,b as default};
