import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 15.2-1308 Incentives for certain joint activities by local governments.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-1308.md","filePath":"vacode/15.2-1308.md"}'),i={name:"vacode/15.2-1308.md"},r=n('<h1 id="§-15-2-1308-incentives-for-certain-joint-activities-by-local-governments" tabindex="-1">§ 15.2-1308 Incentives for certain joint activities by local governments. <a class="header-anchor" href="#§-15-2-1308-incentives-for-certain-joint-activities-by-local-governments" aria-label="Permalink to &quot;§ 15.2-1308 Incentives for certain joint activities by local governments.&quot;">​</a></h1><p>A. The General Assembly may establish a fund to be used to encourage regional strategic planning and cooperation. Specifically, the incentive fund shall be used to encourage and reward regional strategic economic development planning and joint activities as described in § <a href="http://law.lis.virginia.gov/vacode/15.2-1309/" target="_blank" rel="noreferrer">15.2-1309</a>.</p><p>B. The fund shall be administered by the Department of Housing and Community Development and distributed to the qualifying localities in installments under the terms and conditions of applicable statutes and by procedures adopted by the Department.</p><p>C. All departments, agencies, institutions, and local governments of the Commonwealth shall make available such information and assistance as the Department may request in the performance of its responsibilities set forth in this section.</p><p>1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP1045" target="_blank" rel="noreferrer">1045</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP1055" target="_blank" rel="noreferrer">1055</a>, § 15.1-1227.3; 1997, c. 587; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0055" target="_blank" rel="noreferrer">55</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0077" target="_blank" rel="noreferrer">77</a>.</p>',5),o=[r];function s(l,c,d,p,g,f){return t(),a("div",null,o)}const m=e(i,[["render",s]]);export{v as __pageData,m as default};
