import{_ as e,o as i,c as t,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 15.2-1500.1 Employment discrimination prohibited; sexual orientation or gender identity.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-1500.1.md","filePath":"vacode/15.2-1500.1.md"}'),a={name:"vacode/15.2-1500.1.md"},r=n('<h1 id="§-15-2-1500-1-employment-discrimination-prohibited-sexual-orientation-or-gender-identity" tabindex="-1">§ 15.2-1500.1 Employment discrimination prohibited; sexual orientation or gender identity. <a class="header-anchor" href="#§-15-2-1500-1-employment-discrimination-prohibited-sexual-orientation-or-gender-identity" aria-label="Permalink to &quot;§ 15.2-1500.1 Employment discrimination prohibited; sexual orientation or gender identity.&quot;">​</a></h1><p>A. As used in this article, unless the context requires a different meaning:</p><p>&quot;Age&quot; means being an individual who is at least 40 years of age.</p><p>&quot;Military status&quot; means status as (i) a member of the uniformed forces, as defined in 10 U.S.C. § 101(a)(5), of the United States or a reserve component thereof named under 10 U.S.C. § 10101, (ii) a veteran as defined in 38 U.S.C. § 101(2), or (iii) a dependent as defined in 50 U.S.C. § 3911(4) except that the support provided by the service member to the individual shall have been provided 180 days immediately preceding an alleged action that if proven true would constitute unlawful discrimination under this section instead of 180 days immediately preceding an application for relief under 50 U.S.C. Chapter 50.</p><p>&quot;Religion&quot; includes any outward expression of religious faith, including adherence to religious dressing and grooming practices and the carrying or display of religious items or symbols.</p><p>B. No department, office, board, commission, agency, or instrumentality of local government shall discriminate in employment on the basis of race, color, religion, national origin, sex, pregnancy, childbirth or related medical conditions, age, marital status, disability, sexual orientation, gender identity, or military status.</p><p>C. The provisions of this section shall not prohibit (i) discrimination in employment on the basis of sex or age in those instances when sex or age is a bona fide occupational qualification for employment or (ii) providing preference in employment to veterans.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1137" target="_blank" rel="noreferrer">1137</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1140" target="_blank" rel="noreferrer">1140</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0477" target="_blank" rel="noreferrer">477</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0478" target="_blank" rel="noreferrer">478</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0799" target="_blank" rel="noreferrer">799</a>.</p>',8),o=[r];function s(l,d,c,p,m,g){return i(),t("div",null,o)}const u=e(a,[["render",s]]);export{f as __pageData,u as default};
