import{_ as i,o,c as n,z as e,a as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 15.2-1505.1 Applicant preemployment information.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-1505.1.md","filePath":"vacode/15.2-1505.1.md"}'),a={name:"vacode/15.2-1505.1.md"},r=e("h1",{id:"§-15-2-1505-1-applicant-preemployment-information",tabindex:"-1"},[t("§ 15.2-1505.1 Applicant preemployment information. "),e("a",{class:"header-anchor",href:"#§-15-2-1505-1-applicant-preemployment-information","aria-label":'Permalink to "§ 15.2-1505.1 Applicant preemployment information."'},"​")],-1),s=e("p",null,[t("A locality may by ordinance, and in accordance with § "),e("a",{href:"http://law.lis.virginia.gov/vacode/19.2-389/",target:"_blank",rel:"noreferrer"},"19.2-389"),t(", require applicants upon offer of employment with the locality to submit to fingerprinting and to provide personal descriptive information to be forwarded along with the applicant's fingerprints through the Central Criminal Records Exchange and the Federal Bureau of Investigation for the purpose of obtaining criminal history record information regarding such applicant. Such applicants shall, if required by ordinance, pay the cost of the fingerprinting or criminal records check or both.")],-1),c=e("p",null,"The Central Criminal Records Exchange, upon receipt of an applicant's record or notification that no record exists, shall make a report to the chief administrative officer of the locality or his designee, who must belong to a governmental entity. In determining whether a criminal conviction directly relates to a position, the locality shall consider the following criteria: (i) the nature and seriousness of the crime; (ii) the relationship of the crime to the work to be performed in the position applied for; (iii) the extent to which the position applied for might offer an opportunity to engage in further criminal activity of the same type as that in which the person had been involved; (iv) the relationship of the crime to the ability, capacity or fitness required to perform the duties and discharge the responsibilities of the position being sought; (v) the extent and nature of the person's past criminal activity; (vi) the age of the person at the time of the commission of the crime; (vii) the amount of time that has elapsed since the person's last involvement in the commission of a crime; (viii) the conduct and work activity of the person prior to and following the criminal activity; and (ix) evidence of the person's rehabilitation or rehabilitative effort while incarcerated or following release.",-1),h=e("p",null,"If an applicant is denied employment because of information appearing in his criminal history record, the locality shall notify the applicant that information obtained from the Central Criminal Records Exchange contributed to such denial. The information shall not be disseminated except as provided for in this section.",-1),l=e("p",null,[t("2003, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0739",target:"_blank",rel:"noreferrer"},"739"),t(".")],-1),p=[r,s,c,h,l];function d(f,m,g,u,v,y){return o(),n("div",null,p)}const w=i(a,[["render",d]]);export{b as __pageData,w as default};
