import{_ as e,o,c as i,O as r}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 15.2-1518 Liability insurance for officers, employees and volunteers of local government and members of its boards and commissions and constitutional officers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-1518.md","filePath":"vacode/15.2-1518.md"}'),n={name:"vacode/15.2-1518.md"},a=r('<h1 id="§-15-2-1518-liability-insurance-for-officers-employees-and-volunteers-of-local-government-and-members-of-its-boards-and-commissions-and-constitutional-officers" tabindex="-1">§ 15.2-1518 Liability insurance for officers, employees and volunteers of local government and members of its boards and commissions and constitutional officers. <a class="header-anchor" href="#§-15-2-1518-liability-insurance-for-officers-employees-and-volunteers-of-local-government-and-members-of-its-boards-and-commissions-and-constitutional-officers" aria-label="Permalink to &quot;§ 15.2-1518 Liability insurance for officers, employees and volunteers of local government and members of its boards and commissions and constitutional officers.&quot;">​</a></h1><p>Any locality and any political subdivision thereof may provide liability insurance or self-insurance for its fire department operational medical director, police department operational medical director, operational medical director, physician course director for any licensed emergency medical services agency or emergency medical services training program located therein endorsed by the Office of Emergency Medical Services and for its officers, employees and volunteers, including any commission or board, and employees and members thereof, of any authority created or controlled by the local governing body, or any local agency or public service corporation owned, operated or controlled by such local governing body and constitutional officers and their employees.</p><p>The insurance or self-insurance may cover the costs and expenses incident to liability, including those for settlement, suit, or satisfaction of judgment arising from the conduct of such operational medical directors, physician course directors, officers, employees, members or volunteers in the discharge of their official duties.</p><p>For the purposes of this section, &quot;physician course director&quot; or &quot;PCD&quot; means an EMS physician who is responsible for the clinical aspects of emergency medical care training programs, including the clinical and field actions of enrolled students.</p><p>1987, c. 496, § 15.1-7.3:1; 1988, c. 432; 1997, c. 587; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0151" target="_blank" rel="noreferrer">151</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0648" target="_blank" rel="noreferrer">648</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0118" target="_blank" rel="noreferrer">118</a>.</p>',5),s=[a];function t(c,l,d,f,m,p){return o(),i("div",null,s)}const g=e(n,[["render",t]]);export{h as __pageData,g as default};
