import{_ as o,o as n,c as i,z as e,a as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 15.2-1520 Employment of counsel to defend localities and political subdivisions, governing bodies, officers or employees in certain proceedings; costs and expenses of such proceedings.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-1520.md","filePath":"vacode/15.2-1520.md"}'),s={name:"vacode/15.2-1520.md"},r=e("h1",{id:"§-15-2-1520-employment-of-counsel-to-defend-localities-and-political-subdivisions-governing-bodies-officers-or-employees-in-certain-proceedings-costs-and-expenses-of-such-proceedings",tabindex:"-1"},[t("§ 15.2-1520 Employment of counsel to defend localities and political subdivisions, governing bodies, officers or employees in certain proceedings; costs and expenses of such proceedings. "),e("a",{class:"header-anchor",href:"#§-15-2-1520-employment-of-counsel-to-defend-localities-and-political-subdivisions-governing-bodies-officers-or-employees-in-certain-proceedings-costs-and-expenses-of-such-proceedings","aria-label":'Permalink to "§ 15.2-1520 Employment of counsel to defend localities and political subdivisions, governing bodies, officers or employees in certain proceedings; costs and expenses of such proceedings."'},"​")],-1),a=e("p",null,"Notwithstanding any provision of law to the contrary, general or special, a locality, or political subdivision of such locality may employ the county, city or town attorney, or the attorney for the Commonwealth, if there be no county, city or town attorney, or other counsel approved by the governing body to defend it, or any member thereof, or any officer of the locality, or political subdivision or employee thereof, or any trustee or member of any board or commission appointed by the governing body in any legal proceeding to which the governing body, or any member thereof, or any of the foregoing named persons may be a defendant, when such proceeding is instituted against it, or them by virtue of any actions in furtherance of their duties in serving the locality or political subdivision as its governing body or as members thereof or the duties or service of any officer or employee of the locality or political subdivision or any trustee or any member of any board or commission appointed by the governing body.",-1),c=e("p",null,"All costs and expenses of such proceedings so defended shall be charged against the treasury of the locality, or political subdivision and shall be paid out of funds provided therefor by the governing body thereof. Further, in the event any settlement is agreed upon or judgment is rendered against any of the foregoing persons or governing body, the governing body may, in its discretion, pay such settlement or judgment from public funds or other funds or in connection with all of the foregoing may expend public or other funds for insurance or to establish and maintain a self-insurance program to cover such risks or liability.",-1),d=e("p",null,"1968, c. 23, § 15.1-19.2; 1976, c. 544; 1977, c. 47; 1978, c. 442; 1997, c. 587.",-1),l=[r,a,c,d];function f(p,h,y,g,m,u){return n(),i("div",null,l)}const _=o(s,[["render",f]]);export{v as __pageData,_ as default};
