import{_ as t,o,c as s,z as e,a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 15.2-1607 Providing legal fees and expenses for sheriffs and deputies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-1607.md","filePath":"vacode/15.2-1607.md"}'),r={name:"vacode/15.2-1607.md"},i=e("h1",{id:"§-15-2-1607-providing-legal-fees-and-expenses-for-sheriffs-and-deputies",tabindex:"-1"},[a("§ 15.2-1607 Providing legal fees and expenses for sheriffs and deputies. "),e("a",{class:"header-anchor",href:"#§-15-2-1607-providing-legal-fees-and-expenses-for-sheriffs-and-deputies","aria-label":'Permalink to "§ 15.2-1607 Providing legal fees and expenses for sheriffs and deputies."'},"​")],-1),n=e("p",null,"If any sheriff or deputy sheriff is arrested or indicted or otherwise prosecuted on any charge arising out of any act committed in the discharge of his official duties, and such charge is subsequently dismissed or there is rendered a verdict of not guilty, such sheriff or deputy sheriff may submit to the governing body of the locality in which he was elected or appointed a statement of legal fees and expenses incurred in his defense of such charge. The governing body may authorize that such legal fees and expenses, or any portion thereof, be paid from the treasury of such locality. If the affected sheriff or deputy sheriff disagrees with the action of the governing body, the officer may petition the circuit court for the county or city to award the fees and cost. The circuit court, sitting without a jury, shall hold a hearing on the matter. The court for good cause shown may order the governing body to pay all or any appropriate portion of the fees and cost.",-1),d=e("p",null,"1975, c. 30, § 15.1-66.3; 1983, c. 232; 1997, c. 587.",-1),f=[i,n,d];function h(c,l,p,u,g,y){return o(),s("div",null,f)}const v=t(r,[["render",h]]);export{_ as __pageData,v as default};
