import{_ as i,o,c as r,z as e,a as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 15.2-1609.9 Compensation of part-time deputies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-1609.9.md","filePath":"vacode/15.2-1609.9.md"}'),a={name:"vacode/15.2-1609.9.md"},s=e("h1",{id:"§-15-2-1609-9-compensation-of-part-time-deputies",tabindex:"-1"},[t("§ 15.2-1609.9 Compensation of part-time deputies. "),e("a",{class:"header-anchor",href:"#§-15-2-1609-9-compensation-of-part-time-deputies","aria-label":'Permalink to "§ 15.2-1609.9 Compensation of part-time deputies."'},"​")],-1),n=e("p",null,"The part-time deputies of sheriffs shall not receive fixed salaries, but shall be entitled to receive reasonable compensation for their services and allowances for their expenses, to be determined and paid as hereinafter provided. Each such part-time deputy shall keep a record of all services performed by him as such, which shall be reported to the sheriff whose deputy he is. The sheriff shall likewise keep a record of all services performed by each part-time deputy. Each sheriff shall file a monthly report with the board of supervisors or other governing body of the county or city council, as the case may be, on or before the fifth day of the month next succeeding that in which such services are performed, showing in detail all services and hours of service rendered by part-time deputies. The board of supervisors or other governing body or the city council shall recommend to the Compensation Board what in its judgment is a fair compensation to pay each individual part-time deputy of a sheriff on the basis of such reports, except that the limit for compensation per hour of service shall not exceed the hourly equivalent of the minimum annual salary paid a full-time deputy sheriff of like rank and experience who performs like services in the same county or city. In addition, mileage and other expenses for rendering the services shall be paid. If in the judgment of the governing body such limit would work a hardship on a particular part-time deputy sheriff, each sum may be increased with the written approval of the judge of the circuit court of the county or city for which such officer is appointed.",-1),h=e("p",null,[t("Code 1950, § 14-90; 1964, c. 386, § 14.1-78; 1971, Ex. Sess., c. 155; 1972, c. 225; 1980, c. 550; 1998, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0872",target:"_blank",rel:"noreferrer"},"872"),t("; 2017, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0337",target:"_blank",rel:"noreferrer"},"337"),t(".")],-1),c=[s,n,h];function d(l,p,f,m,u,v){return o(),r("div",null,c)}const g=i(a,[["render",d]]);export{b as __pageData,g as default};
