import{_ as e,o as a,c as r,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 15.2-1612 Wearing of same or similar uniforms by unauthorized persons; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-1612.md","filePath":"vacode/15.2-1612.md"}'),o={name:"vacode/15.2-1612.md"},t=i('<h1 id="§-15-2-1612-wearing-of-same-or-similar-uniforms-by-unauthorized-persons-penalty" tabindex="-1">§ 15.2-1612 Wearing of same or similar uniforms by unauthorized persons; penalty. <a class="header-anchor" href="#§-15-2-1612-wearing-of-same-or-similar-uniforms-by-unauthorized-persons-penalty" aria-label="Permalink to &quot;§ 15.2-1612 Wearing of same or similar uniforms by unauthorized persons; penalty.&quot;">​</a></h1><p>Any unauthorized person who wears a uniform identical to or substantially similar to a standard uniform used by an office of sheriff in accordance with § <a href="http://law.lis.virginia.gov/vacode/15.2-1610/" target="_blank" rel="noreferrer">15.2-1610</a> with the intent to deceive a casual observer or with the intent to impersonate the office of sheriff is guilty of a Class 1 misdemeanor. A second or subsequent offense is punishable as a Class 6 felony.</p><p>For purposes of this section, &quot;substantially similar&quot; means so similar in appearance as to be likely to deceive the casual observer.</p><p>1988, c. 626, § 15.1-90.4; 1997, c. 587; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0337" target="_blank" rel="noreferrer">337</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0452" target="_blank" rel="noreferrer">452</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0431" target="_blank" rel="noreferrer">431</a>.</p>',4),s=[t];function n(l,f,c,p,u,h){return a(),r("div",null,s)}const _=e(o,[["render",n]]);export{m as __pageData,_ as default};
