import{_ as e,o as r,c as i,O as o}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 15.2-1709 Employer immunity from liability; disclosure of information regarding former deputy sheriffs and law-enforcement officers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-1709.md","filePath":"vacode/15.2-1709.md"}'),a={name:"vacode/15.2-1709.md"},t=o('<h1 id="§-15-2-1709-employer-immunity-from-liability-disclosure-of-information-regarding-former-deputy-sheriffs-and-law-enforcement-officers" tabindex="-1">§ 15.2-1709 Employer immunity from liability; disclosure of information regarding former deputy sheriffs and law-enforcement officers. <a class="header-anchor" href="#§-15-2-1709-employer-immunity-from-liability-disclosure-of-information-regarding-former-deputy-sheriffs-and-law-enforcement-officers" aria-label="Permalink to &quot;§ 15.2-1709 Employer immunity from liability; disclosure of information regarding former deputy sheriffs and law-enforcement officers.&quot;">​</a></h1><p>Any sheriff or chief of police, any director or chief executive of any agency or department employing deputy sheriffs or law-enforcement officers as defined in § <a href="/vacode/9.1-101/">9.1-101</a> or jail officers as defined in § <a href="/vacode/53.1-1/">53.1-1</a>, and the Director of the Department of Criminal Justice Services or his designee who discloses information about a former deputy sheriff&#39;s or law-enforcement officer&#39;s or jail officer&#39;s job performance or information requested pursuant to subsection B of § <a href="/vacode/15.2-1705/">15.2-1705</a> to a prospective law-enforcement or jail employer of the former appointee or employee is immune from civil liability for such disclosure or its consequences unless the information disclosed by the former employer was knowingly false or deliberately misleading, was rendered with malicious purpose, or violated any civil right of the former employee or appointee.</p><p>1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0850" target="_blank" rel="noreferrer">850</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0905" target="_blank" rel="noreferrer">905</a>, § 15.1-131.8:4; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0112" target="_blank" rel="noreferrer">112</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0587" target="_blank" rel="noreferrer">587</a>; 2020, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?202+ful+CHAP0032" target="_blank" rel="noreferrer">32</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?202+ful+CHAP0037" target="_blank" rel="noreferrer">37</a>.</p>',3),n=[t];function f(l,s,c,m,d,p){return r(),i("div",null,n)}const u=e(a,[["render",f]]);export{h as __pageData,u as default};
