import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 15.2-1716.2 Methamphetamine lab cleanup costs; localities may charge for reimbursement.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-1716.2.md","filePath":"vacode/15.2-1716.2.md"}'),o={name:"vacode/15.2-1716.2.md"},n=r('<h1 id="§-15-2-1716-2-methamphetamine-lab-cleanup-costs-localities-may-charge-for-reimbursement" tabindex="-1">§ 15.2-1716.2 Methamphetamine lab cleanup costs; localities may charge for reimbursement. <a class="header-anchor" href="#§-15-2-1716-2-methamphetamine-lab-cleanup-costs-localities-may-charge-for-reimbursement" aria-label="Permalink to &quot;§ 15.2-1716.2 Methamphetamine lab cleanup costs; localities may charge for reimbursement.&quot;">​</a></h1><p>Any locality may provide by ordinance that any person who is convicted of an offense for manufacture of methamphetamine pursuant to § <a href="http://law.lis.virginia.gov/vacode/18.2-248/" target="_blank" rel="noreferrer">18.2-248</a> or <a href="http://law.lis.virginia.gov/vacode/18.2-248.03/" target="_blank" rel="noreferrer">18.2-248.03</a> shall be liable at the time of sentencing or in a separate civil action to the locality or to any other law-enforcement entity for the expense in cleaning up any methamphetamine lab related to the conviction. The amount charged shall not exceed the actual expenses associated with cleanup, removal, or repair of the affected property or the replacement cost of personal protective equipment used.</p><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0517" target="_blank" rel="noreferrer">517</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0616" target="_blank" rel="noreferrer">616</a>.</p>',3),i=[n];function c(l,s,h,m,p,f){return a(),t("div",null,i)}const u=e(o,[["render",c]]);export{d as __pageData,u as default};
