import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 15.2-1718.2 Receipt of critically missing adult reports.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-1718.2.md","filePath":"vacode/15.2-1718.2.md"}'),r={name:"vacode/15.2-1718.2.md"},n=i('<h1 id="§-15-2-1718-2-receipt-of-critically-missing-adult-reports" tabindex="-1">§ 15.2-1718.2 Receipt of critically missing adult reports. <a class="header-anchor" href="#§-15-2-1718-2-receipt-of-critically-missing-adult-reports" aria-label="Permalink to &quot;§ 15.2-1718.2 Receipt of critically missing adult reports.&quot;">​</a></h1><p>A. No police or sheriff&#39;s department shall establish or maintain any policy that requires the observance of any waiting period before accepting a critically missing adult report. Upon receipt of a critically missing adult report by any police or sheriff&#39;s department, the department shall immediately, but in all cases within two hours of receiving the report, enter identifying and descriptive data about the critically missing adult into the Virginia Criminal Information Network and the National Crime Information Center Systems, forward the report to the Department of State Police, notify all other law-enforcement agencies in the area, and initiate an investigation of the case.</p><p>B. For purposes of this section:</p><p>&quot;Critically missing adult&quot; means any missing adult, including an adult who has a developmental disability, intellectual disability, or mental illness as those terms are defined in § <a href="/vacode/37.2-100/">37.2-100</a>, 18 years of age or older whose disappearance indicates a credible threat to the health and safety of the adult as determined by a law-enforcement agency and under such other circumstances as deemed appropriate after consideration of all known circumstances.</p><p>&quot;Critically missing adult report&quot; means a report prepared in a format prescribed by the Superintendent of State Police for use by law-enforcement agencies to report critically missing adult information, including a photograph, to the Department of State Police.</p><p>2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0205" target="_blank" rel="noreferrer">205</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0223" target="_blank" rel="noreferrer">223</a>; 2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0394" target="_blank" rel="noreferrer">394</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0395" target="_blank" rel="noreferrer">395</a>.</p>',6),o=[n];function l(s,c,p,d,f,h){return t(),a("div",null,o)}const u=e(r,[["render",l]]);export{g as __pageData,u as default};
