import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 15.2-1718 Receipt of missing child reports.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-1718.md","filePath":"vacode/15.2-1718.md"}'),a={name:"vacode/15.2-1718.md"},n=r('<h1 id="§-15-2-1718-receipt-of-missing-child-reports" tabindex="-1">§ 15.2-1718 Receipt of missing child reports. <a class="header-anchor" href="#§-15-2-1718-receipt-of-missing-child-reports" aria-label="Permalink to &quot;§ 15.2-1718 Receipt of missing child reports.&quot;">​</a></h1><p>No police or sheriff&#39;s department shall establish or maintain any policy which requires the observance of any waiting period before accepting a missing child report as defined in § <a href="http://law.lis.virginia.gov/vacode/52-32/" target="_blank" rel="noreferrer">52-32</a>. Upon receipt of a missing child report by any police or sheriff&#39;s department, the department shall immediately, but in all cases within two hours of receiving the report, enter identifying and descriptive data about the child into the Virginia Criminal Information Network and the National Crime Information Center Systems, forward the report to the Missing Children Information Clearinghouse within the Department of State Police, notify all other law-enforcement agencies in the area, and initiate an investigation of the case.</p><p>1985, c. 259, § 15.1-131.9; 1990, c. 239; 1997, c. 587; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0248" target="_blank" rel="noreferrer">248</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0443" target="_blank" rel="noreferrer">443</a>.</p>',3),o=[n];function s(c,l,h,p,d,f){return i(),t("div",null,o)}const g=e(a,[["render",s]]);export{m as __pageData,g as default};
