import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 15.2-1722.1 Prohibited practices; collection of data.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-1722.1.md","filePath":"vacode/15.2-1722.1.md"}'),i={name:"vacode/15.2-1722.1.md"},r=o('<h1 id="§-15-2-1722-1-prohibited-practices-collection-of-data" tabindex="-1">§ 15.2-1722.1 Prohibited practices; collection of data. <a class="header-anchor" href="#§-15-2-1722-1-prohibited-practices-collection-of-data" aria-label="Permalink to &quot;§ 15.2-1722.1 Prohibited practices; collection of data.&quot;">​</a></h1><p>A. No law-enforcement officer shall engage in bias-based profiling as defined in § <a href="/vacode/52-30.1/">52-30.1</a> in the performance of his official duties.</p><p>B. The police force of every locality shall collect data pertaining to (i) all investigatory motor vehicle stops, (ii) all stop-and-frisks of a person based on reasonable suspicion, and (iii) all other investigatory detentions that do not result in an arrest or the issuance of a summons pursuant to § <a href="/vacode/52-30.2/">52-30.2</a> and report such data to the Department of State Police for inclusion in the Community Policing Reporting Database established pursuant to § <a href="/vacode/52-30.3/">52-30.3</a>. The chief of police of the locality shall be responsible for forwarding the data to the Superintendent of State Police.</p><p>C. The chief of police of the locality shall post the data that has been forwarded for inclusion in the Community Policing Reporting Database on a website that is maintained by the chief of police or on any other website on which the chief of police generally posts information and that is available to the public or that clearly describes how the public may access such data.</p><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1165" target="_blank" rel="noreferrer">1165</a>; 2020, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?202+ful+CHAP0037" target="_blank" rel="noreferrer">37</a>.</p>',5),n=[r];function c(s,l,h,d,f,p){return a(),t("div",null,n)}const u=e(i,[["render",c]]);export{b as __pageData,u as default};
