import{_ as o,o as a,c as t,z as e,a as n}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 15.2-1810 Leasing land for swimming pool purposes.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-1810.md","filePath":"vacode/15.2-1810.md"}'),s={name:"vacode/15.2-1810.md"},i=e("h1",{id:"§-15-2-1810-leasing-land-for-swimming-pool-purposes",tabindex:"-1"},[n("§ 15.2-1810 Leasing land for swimming pool purposes. "),e("a",{class:"header-anchor",href:"#§-15-2-1810-leasing-land-for-swimming-pool-purposes","aria-label":'Permalink to "§ 15.2-1810 Leasing land for swimming pool purposes."'},"​")],-1),r=e("p",null,"Any locality, in its discretion, may lease to any responsible person, firm or corporation any lands owned or held by such locality for the purpose of constructing or erecting thereon a swimming pool and buildings and improvements incident thereto. The terms and provisions of any such lease shall be prescribed by the governing body, provided that any such lease contains a clause to the effect that at the termination of such lease it shall not be renewed and that the land and all improvements thereon shall revert to the locality and shall be free from any encumbrance at the time of such reversion. All moneys received by a locality under this section shall constitute a fund for the development and improvement of recreational facilities within such locality.",-1),l=e("p",null,"Code 1950, § 15-691.1; 1954, c. 410; 1962, c. 623, § 15.1-261; 1997, c. 587.",-1),c=[i,r,l];function d(h,p,m,f,u,_){return a(),t("div",null,c)}const v=o(s,[["render",d]]);export{y as __pageData,v as default};
