import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 15.2-2009.1 Dangerous roadside vegetation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2009.1.md","filePath":"vacode/15.2-2009.1.md"}'),r={name:"vacode/15.2-2009.1.md"},i=o('<h1 id="§-15-2-2009-1-dangerous-roadside-vegetation" tabindex="-1">§ 15.2-2009.1 Dangerous roadside vegetation. <a class="header-anchor" href="#§-15-2-2009-1-dangerous-roadside-vegetation" aria-label="Permalink to &quot;§ 15.2-2009.1 Dangerous roadside vegetation.&quot;">​</a></h1><p>Notwithstanding the provisions of subsection A of § <a href="/vacode/15.2-2000/">15.2-2000</a>, any locality may, by ordinance, provide that the owner of any property adjacent to the right-of-way of any street, highway, road, alley, bridge, viaduct, subway, underpass, or other public right-of-way or place shall, at such time or times as the governing body may prescribe, remove therefrom any and all trees, tree limbs, shrubs, high grass, or other substance that might dangerously obstruct the line of sight of a driver, be involved in a collision with a vehicle, or interfere with the safe operation of a vehicle or may, whenever the governing body deems it necessary, after reasonable notice as defined in subdivision 2 of § <a href="/vacode/15.2-906/">15.2-906</a>, have such trees, shrubs, high grass, and other like substances removed by its own agents or employees.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0962" target="_blank" rel="noreferrer">962</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0963" target="_blank" rel="noreferrer">963</a>.</p>',3),s=[i];function n(d,h,c,l,g,f){return a(),t("div",null,s)}const p=e(r,[["render",n]]);export{v as __pageData,p as default};
