import{_ as i,o as t,c as r,z as e,a}from"./chunks/framework.9482e208.js";const w=JSON.parse('{"title":"§ 15.2-2102 How bids received and to whom franchise awarded.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2102.md","filePath":"vacode/15.2-2102.md"}'),o={name:"vacode/15.2-2102.md"},s=e("h1",{id:"§-15-2-2102-how-bids-received-and-to-whom-franchise-awarded",tabindex:"-1"},[a("§ 15.2-2102 How bids received and to whom franchise awarded. "),e("a",{class:"header-anchor",href:"#§-15-2-2102-how-bids-received-and-to-whom-franchise-awarded","aria-label":'Permalink to "§ 15.2-2102 How bids received and to whom franchise awarded."'},"​")],-1),n=e("p",null,"The presiding officer shall read aloud, or cause to be read aloud, a brief summary of each of the bids that have been received, for public information, and shall then inquire if any further bids are offered. If further bids are offered, they shall be received. The presiding officer shall thereafter declare the bidding closed. The presiding officer shall receive recommendations from the staff relative to any bids received in advance and staff's recommendations, if any, on any bids received at the advertised council meeting. After such other investigation as the council sees fit to make, the council shall accept the highest bid from a responsible bidder and shall adopt the ordinance as advertised, without substantial variation, except to insert the name of the accepted bidder. However, the council, by a recorded vote of a majority of the members elected to the council, may reject a higher bid and accept a lower bid from a responsible bidder and award the franchise, right, lease or privilege to the lower bidder, if, in its opinion, some reason affecting the interest of the city or town makes it advisable to do so, which reason shall be expressed in the body of the subsequent ordinance granting the franchise, right, lease or privilege. The process described in this section may run concurrently with any other advertisement or public ordinance requirements of this title, or such requirements as may be contained in charters of such cities or towns.",-1),d=e("p",null,[a("Code 1950, § 15-730; 1962, c. 623, § 15.1-310; 1997, c. 587; 2001, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0498",target:"_blank",rel:"noreferrer"},"498"),a(".")],-1),c=[s,n,d];function h(l,f,b,m,u,p){return t(),r("div",null,c)}const _=i(o,[["render",h]]);export{w as __pageData,_ as default};
