import{_ as e,o as r,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 15.2-2103.1 Solar services agreements; nondisclosure of proprietary information.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2103.1.md","filePath":"vacode/15.2-2103.1.md"}'),t={name:"vacode/15.2-2103.1.md"},i=o('<h1 id="§-15-2-2103-1-solar-services-agreements-nondisclosure-of-proprietary-information" tabindex="-1">§ 15.2-2103.1 Solar services agreements; nondisclosure of proprietary information. <a class="header-anchor" href="#§-15-2-2103-1-solar-services-agreements-nondisclosure-of-proprietary-information" aria-label="Permalink to &quot;§ 15.2-2103.1 Solar services agreements; nondisclosure of proprietary information.&quot;">​</a></h1><p>A. A solar services agreement may be structured as a service agreement or may be subject to available appropriation.</p><p>B. Nothing in this article shall be construed to require the disclosure of proprietary information voluntarily provided by a private entity in connection with a franchise, lease, or use under a solar services agreement that is excluded from mandatory disclosure pursuant to subdivision 29 of § <a href="http://law.lis.virginia.gov/vacode/2.2-3705.6/" target="_blank" rel="noreferrer">2.2-3705.6</a> of the Virginia Freedom of Information Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-3700/" target="_blank" rel="noreferrer">2.2-3700</a> et seq.).</p><p>C. Nothing in this section, however, shall be construed as authorizing the withholding of the financial terms of such agreements.</p><p>2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0737" target="_blank" rel="noreferrer">737</a>.</p>',5),n=[i];function s(c,l,p,d,f,h){return r(),a("div",null,n)}const u=e(t,[["render",s]]);export{m as __pageData,u as default};
