import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 15.2-2114.01 Local Stormwater Management Fund; grant moneys.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2114.01.md","filePath":"vacode/15.2-2114.01.md"}'),o={name:"vacode/15.2-2114.01.md"},n=r('<h1 id="§-15-2-2114-01-local-stormwater-management-fund-grant-moneys" tabindex="-1">§ 15.2-2114.01 Local Stormwater Management Fund; grant moneys. <a class="header-anchor" href="#§-15-2-2114-01-local-stormwater-management-fund-grant-moneys" aria-label="Permalink to &quot;§ 15.2-2114.01 Local Stormwater Management Fund; grant moneys.&quot;">​</a></h1><p>Any locality may by ordinance create a local Stormwater Management Fund consisting of appropriated local moneys for the purpose of granting funds to an owner of private property or a common interest community for stormwater management and erosion prevention on previously developed lands. Grants from such fund shall be used only for (i) the construction, improvement, or repair of a stormwater management facility; (ii) erosion and sediment control; or (iii) flood mitigation and protection measures that are part of a comprehensive flood mitigation and protection plan adopted by the locality. Grants made pursuant to clause (iii) shall, where practicable, prioritize projects that include nature-based practices.</p><p>2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0344" target="_blank" rel="noreferrer">344</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0559" target="_blank" rel="noreferrer">559</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0380" target="_blank" rel="noreferrer">380</a>.</p>',3),i=[n];function s(c,l,m,p,d,f){return a(),t("div",null,i)}const u=e(o,[["render",s]]);export{_ as __pageData,u as default};
