import{_ as a,o as i,c as s,z as e,a as t}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 15.2-2118.1 Lien for gas utilities charges and taxes imposed by localities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2118.1.md","filePath":"vacode/15.2-2118.1.md"}'),r={name:"vacode/15.2-2118.1.md"},o=e("h1",{id:"§-15-2-2118-1-lien-for-gas-utilities-charges-and-taxes-imposed-by-localities",tabindex:"-1"},[t("§ 15.2-2118.1 Lien for gas utilities charges and taxes imposed by localities. "),e("a",{class:"header-anchor",href:"#§-15-2-2118-1-lien-for-gas-utilities-charges-and-taxes-imposed-by-localities","aria-label":'Permalink to "§ 15.2-2118.1 Lien for gas utilities charges and taxes imposed by localities."'},"​")],-1),n=e("p",null,"The governing body of any locality with a municipally-owned gas utility may by ordinance provide that taxes or charges hereafter made, imposed or incurred for gas service within or outside such locality shall be a lien on the real estate served by such gas utility. Where residential rental real estate is involved, no lien shall attach (i) unless the user of the gas utility services is also the owner of the real estate or (ii) unless the owner of the real estate negotiated or executed the agreement by which such gas utility services were provided to the property. Nothing herein shall authorize a locality to require that municipal gas service be contracted for in the name of the owner of residential rental real estate if the lease between the owner and any tenant for such residential rental real estate provides that the tenant shall contract for such gas service.",-1),l=e("p",null,[t("2001, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0761",target:"_blank",rel:"noreferrer"},"761"),t(".")],-1),c=[o,n,l];function h(d,u,f,g,p,_){return i(),s("div",null,c)}const v=a(r,[["render",h]]);export{y as __pageData,v as default};
