import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 15.2-2121 Regulations as to water, sewer and other facilities in subdivisions and development plans.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2121.md","filePath":"vacode/15.2-2121.md"}'),n={name:"vacode/15.2-2121.md"},o=i('<h1 id="§-15-2-2121-regulations-as-to-water-sewer-and-other-facilities-in-subdivisions-and-development-plans" tabindex="-1">§ 15.2-2121 Regulations as to water, sewer and other facilities in subdivisions and development plans. <a class="header-anchor" href="#§-15-2-2121-regulations-as-to-water-sewer-and-other-facilities-in-subdivisions-and-development-plans" aria-label="Permalink to &quot;§ 15.2-2121 Regulations as to water, sewer and other facilities in subdivisions and development plans.&quot;">​</a></h1><p>Any locality which has adopted regulations under Chapter 22 (§ <a href="http://law.lis.virginia.gov/vacode/15.2-2200/" target="_blank" rel="noreferrer">15.2-2200</a> et seq.) governing the use and development of land may also adopt regulations, subject to the provisions of Chapter 3.1 (§ <a href="http://law.lis.virginia.gov/vacode/62.1-44.2/" target="_blank" rel="noreferrer">62.1-44.2</a> et seq.) of Title 62.1, fixing requirements as to the extent to which and the manner in which water, sewer and other utility mains, piping, conduits, connections, pumping stations and other facilities in connection therewith shall be installed as a condition precedent to the approval of an original plat of a subdivision or a development plan adopted pursuant to § <a href="http://law.lis.virginia.gov/vacode/15.2-2286/" target="_blank" rel="noreferrer">15.2-2286</a>, or alteration of any such plat or a development plan adopted pursuant to § <a href="http://law.lis.virginia.gov/vacode/15.2-2286/" target="_blank" rel="noreferrer">15.2-2286</a>. Such regulations may (i) require the water source to be an approved source of supply capable of furnishing the needs of the eventual inhabitants of such subdivision proposed to be served thereby, (ii) include requirements as to the size and nature of the water and sewer and other utility mains, pipes, conduits, connections, pumping stations or other facilities installed or to be installed in connection with the proposed water or sewer systems and (iii) include requirements to extend and connect to abutting or adjacent public water or sewer systems.</p><p>Code 1950, § 15-719.1; 1954, c. 592; 1962, c. 623, § 15.1-299; 1970, c. 572; 1997, c. 587; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0567" target="_blank" rel="noreferrer">567</a>.</p>',3),r=[o];function s(l,d,c,p,h,u){return t(),a("div",null,r)}const g=e(n,[["render",s]]);export{v as __pageData,g as default};
