import{_ as a,o,c as n,z as e,a as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 15.2-2123 Sewage treatment plants to include certain capability.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2123.md","filePath":"vacode/15.2-2123.md"}'),i={name:"vacode/15.2-2123.md"},r=e("h1",{id:"§-15-2-2123-sewage-treatment-plants-to-include-certain-capability",tabindex:"-1"},[t("§ 15.2-2123 Sewage treatment plants to include certain capability. "),e("a",{class:"header-anchor",href:"#§-15-2-2123-sewage-treatment-plants-to-include-certain-capability","aria-label":'Permalink to "§ 15.2-2123 Sewage treatment plants to include certain capability."'},"​")],-1),s=e("p",null,"Whenever the governing body of a locality or a combination of governing bodies of two or more localities is using the authority of this chapter to construct a new sewage treatment plant, or a hydraulic expansion or major upgrade of an existing sewage treatment plant, the facility shall be designed and constructed so that it has the capability to treat the septage from all onsite sewage disposal systems, which are not adequately served by another approved disposal site, located in the area of the locality or combination thereof to be served by such plant. However, the locality or combination thereof may limit the amount of septage that the sewage treatment plant is designed to accept in order to eliminate or reduce a disproportionate engineering, design, or fiscal burden that may be placed on the sewage treatment plant or its users, to utilize cost-effective regional approaches, or to address engineering design considerations including protection of biological treatment processes.",-1),l=e("p",null,[t("The locality or combination thereof shall notify the Department of Environmental Quality of the septage treatment capability of the sewage treatment plant prior to the Department's issuance of a state certificate to construct for such new, expanded, or upgraded facility. The locality or combination thereof shall provide a copy of such notification to the Board of Health to assist the Board of Health in its long-range planning pursuant to § "),e("a",{href:"http://law.lis.virginia.gov/vacode/32.1-163.2/",target:"_blank",rel:"noreferrer"},"32.1-163.2"),t(".")],-1),c=e("p",null,"This notification requirement shall not apply to any new project for which a preliminary engineering report has been submitted to the Department of Environmental Quality on or before December 31, 2008.",-1),p=e("p",null,[t("1986, c. 329, § 15.1-317.1; 1997, c. 587; 2008, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0389",target:"_blank",rel:"noreferrer"},"389"),t(".")],-1),h=[r,s,l,c,p];function d(f,g,m,u,b,y){return o(),n("div",null,h)}const w=a(i,[["render",d]]);export{v as __pageData,w as default};
