import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 15.2-2140 Dispute between jurisdictions; appeals.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2140.md","filePath":"vacode/15.2-2140.md"}'),i={name:"vacode/15.2-2140.md"},s=r('<h1 id="§-15-2-2140-dispute-between-jurisdictions-appeals" tabindex="-1">§ 15.2-2140 Dispute between jurisdictions; appeals. <a class="header-anchor" href="#§-15-2-2140-dispute-between-jurisdictions-appeals" aria-label="Permalink to &quot;§ 15.2-2140 Dispute between jurisdictions; appeals.&quot;">​</a></h1><p>A. An appeal may be filed in the Court of Appeals by any party from the judgment of the special court, and the appeal shall be heard and determined without reference to the principles of demurrer to evidence. The special court shall certify the facts in the case to the Court of Appeals, and the evidence shall be considered as on appeal in proceedings under Chapter 2 (§ <a href="/vacode/25.1-200/">25.1-200</a> et seq.) of Title 25.1. In any case, by consent of all parties of record, a motion to dismiss may be made at any time before final judgment on appeal.</p><p>B. If the judgment of the special court is reversed on appeal, or if the judgment is modified, the Court of Appeals shall enter such order as the special court should have entered.</p><p>C. If an appeal is taken from the judgment of the Court of Appeals, the Supreme Court, in matters in which it grants the petition for appeal, shall consider the appeal consistent with the procedures set forth in subsection A and shall enter such order as the special court should have entered.</p><p>1976, c. 69, § 15.1-37.1:6; 1979, c. 671; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0587" target="_blank" rel="noreferrer">587</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0940" target="_blank" rel="noreferrer">940</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0489" target="_blank" rel="noreferrer">489</a>.</p>',5),o=[s];function n(p,l,c,d,h,u){return t(),a("div",null,o)}const m=e(i,[["render",n]]);export{_ as __pageData,m as default};
