import{_ as a,o as t,c as r,z as e,a as n}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 15.2-2209.2 Public infrastructure maintenance bonds.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2209.2.md","filePath":"vacode/15.2-2209.2.md"}'),i={name:"vacode/15.2-2209.2.md"},o=e("h1",{id:"§-15-2-2209-2-public-infrastructure-maintenance-bonds",tabindex:"-1"},[n("§ 15.2-2209.2 Public infrastructure maintenance bonds. "),e("a",{class:"header-anchor",href:"#§-15-2-2209-2-public-infrastructure-maintenance-bonds","aria-label":'Permalink to "§ 15.2-2209.2 Public infrastructure maintenance bonds."'},"​")],-1),s=e("p",null,"In order to protect existing public infrastructure, the City of Charlottesville may by ordinance require public infrastructure maintenance bonds from developers and property owners in conjunction with the construction of single-family and two-family homes in instances where the provisions of a subdivision ordinance are not applicable and all required performance and maintenance bonds have been released. Such maintenance bonds shall not exceed an amount reasonably necessary to maintain and repair publicly owned streets, sidewalks, and infrastructure depicted or provided for in the approved plan, plat, permit application, or similar document for which such bond is applicable, on site or immediately adjacent to the construction, and shall not be used for the purpose of repairing damage to infrastructure that preexisted the construction, unless otherwise agreed upon by the developer, property owner, and the locality. The maximum bond shall not exceed $5,000 and shall only be required at the time of issuance of a certificate of occupancy. The ordinance shall make provision for the inspection of bonded improvements within five business days of completion and the release of any performance guarantee within five business days of such inspection.",-1),c=e("p",null,[n("2011, cc. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0692",target:"_blank",rel:"noreferrer"},"692"),n(", "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0711",target:"_blank",rel:"noreferrer"},"711"),n(".")],-1),l=[o,s,c];function d(u,p,h,f,m,b){return t(),r("div",null,l)}const y=a(i,[["render",d]]);export{v as __pageData,y as default};
