import{_ as e,o as a,c as o,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 15.2-2288.5 Meaning of \\"cemetery\\" for purposes of zoning.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2288.5.md","filePath":"vacode/15.2-2288.5.md"}'),t={name:"vacode/15.2-2288.5.md"},n=r('<h1 id="§-15-2-2288-5-meaning-of-cemetery-for-purposes-of-zoning" tabindex="-1">§ 15.2-2288.5 Meaning of &quot;cemetery&quot; for purposes of zoning. <a class="header-anchor" href="#§-15-2-2288-5-meaning-of-cemetery-for-purposes-of-zoning" aria-label="Permalink to &quot;§ 15.2-2288.5 Meaning of &quot;cemetery&quot; for purposes of zoning.&quot;">​</a></h1><p>A. A &quot;cemetery&quot; for purposes of this chapter shall have the meaning set forth in § <a href="http://law.lis.virginia.gov/vacode/54.1-2310/" target="_blank" rel="noreferrer">54.1-2310</a>.</p><p>B. Nothing in this section shall exempt a licensed funeral home or cemetery from any applicable zoning regulation.</p><p>C. The following uses shall be included in the approval of a cemetery without further zoning approval being required: all uses necessarily or customarily associated with interment of human remains, benches, ledges, walls, graves, roads, paths, landscaping, and soil storage consistent with federal, state, and local laws on erosion sediment control.</p><p>D. Mausoleums, columbaria, chapels, administrative offices, and maintenance and storage areas that are shown in a legislative approval for the specific cemetery obtained at the request of the owner shall not require additional local legislative approval provided such structures and uses are developed in accordance with the original local legislative approval. This subsection shall not supersede any permission required by an ordinance adopted pursuant to § <a href="http://law.lis.virginia.gov/vacode/15.2-2306/" target="_blank" rel="noreferrer">15.2-2306</a> relative to historic districts.</p><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0414" target="_blank" rel="noreferrer">414</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0478" target="_blank" rel="noreferrer">478</a>.</p>',6),i=[n];function s(l,c,p,h,d,f){return a(),o("div",null,i)}const m=e(t,[["render",s]]);export{g as __pageData,m as default};
