import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 15.2-2288.6 Agricultural operations; local regulation of certain activities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2288.6.md","filePath":"vacode/15.2-2288.6.md"}'),r={name:"vacode/15.2-2288.6.md"},o=i('<h1 id="§-15-2-2288-6-agricultural-operations-local-regulation-of-certain-activities" tabindex="-1">§ 15.2-2288.6 Agricultural operations; local regulation of certain activities. <a class="header-anchor" href="#§-15-2-2288-6-agricultural-operations-local-regulation-of-certain-activities" aria-label="Permalink to &quot;§ 15.2-2288.6 Agricultural operations; local regulation of certain activities.&quot;">​</a></h1><p>A. No locality shall regulate the carrying out of any of the following activities at an agricultural operation, as defined in § <a href="/vacode/3.2-300/">3.2-300</a>, unless there is a substantial impact on the health, safety, or general welfare of the public:</p><ol><li><p>Agritourism activities as defined in § <a href="/vacode/3.2-6400/">3.2-6400</a>;</p></li><li><p>The sale of agricultural or silvicultural products, or the sale of agricultural-related or silvicultural-related items incidental to the agricultural operation;</p></li><li><p>The preparation, processing, or sale of food products in compliance with subdivisions C 3, 4, and 5 of § <a href="/vacode/3.2-5130/">3.2-5130</a> or related state laws and regulations; or</p></li><li><p>Other activities or events that are usual and customary at Virginia agricultural operations.</p></li></ol><p>Any local restriction placed on an activity listed in this subsection shall be reasonable and shall take into account the economic impact of the restriction on the agricultural operation and the agricultural nature of the activity.</p><p>B. No locality shall require a special exception, administrative permit not required by state law, or special use permit for any activity listed in subsection A on property that is zoned as an agricultural district or classification unless there is a substantial impact on the health, safety, or general welfare of the public.</p><p>C. Except regarding the sound generated by outdoor amplified music, no local ordinance regulating the sound generated by any activity listed in subsection A shall be more restrictive than the general noise ordinance of the locality. In permitting outdoor amplified music at an agricultural operation, the locality shall consider the effect on adjoining property owners and nearby residents.</p><p>D. The provisions of this section shall not affect any entity licensed in accordance with Chapter 2 (§ <a href="/vacode/4.1-200/">4.1-200</a> et seq.) of Title 4.1. Nothing in this section shall be construed to affect the provisions of Chapter 3 (§ <a href="/vacode/3.2-300/">3.2-300</a> et seq.) of Title 3.2, to alter the provisions of § <a href="/vacode/15.2-2288.3/">15.2-2288.3</a>, or to restrict the authority of any locality under Title 58.1.</p><p>2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0153" target="_blank" rel="noreferrer">153</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0494" target="_blank" rel="noreferrer">494</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0204" target="_blank" rel="noreferrer">204</a>.</p>',8),l=[o];function n(s,c,u,p,h,d){return a(),t("div",null,l)}const v=e(r,[["render",n]]);export{g as __pageData,v as default};
