import{_ as e,o as i,c as r,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 15.2-2291 Assisted living facilities and group homes of eight or fewer; single-family residence.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2291.md","filePath":"vacode/15.2-2291.md"}'),t={name:"vacode/15.2-2291.md"},o=a('<h1 id="§-15-2-2291-assisted-living-facilities-and-group-homes-of-eight-or-fewer-single-family-residence" tabindex="-1">§ 15.2-2291 Assisted living facilities and group homes of eight or fewer; single-family residence. <a class="header-anchor" href="#§-15-2-2291-assisted-living-facilities-and-group-homes-of-eight-or-fewer-single-family-residence" aria-label="Permalink to &quot;§ 15.2-2291 Assisted living facilities and group homes of eight or fewer; single-family residence.&quot;">​</a></h1><p>A. Zoning ordinances for all purposes shall consider a residential facility in which no more than eight individuals with mental illness, intellectual disability, or developmental disabilities reside, with one or more resident or nonresident staff persons, as residential occupancy by a single family. For the purposes of this subsection, mental illness and developmental disability shall not include current illegal use of or addiction to a controlled substance as defined in § <a href="http://law.lis.virginia.gov/vacode/54.1-3401/" target="_blank" rel="noreferrer">54.1-3401</a>. No conditions more restrictive than those imposed on residences occupied by persons related by blood, marriage, or adoption shall be imposed on such facility. For purposes of this subsection, &quot;residential facility&quot; means any group home or other residential facility for which the Department of Behavioral Health and Developmental Services is the licensing authority pursuant to this Code.</p><p>B. Zoning ordinances for all purposes shall consider a residential facility in which no more than eight aged, infirm or disabled persons reside, with one or more resident counselors or other staff persons, as residential occupancy by a single family. No conditions more restrictive than those imposed on residences occupied by persons related by blood, marriage, or adoption shall be imposed on such facility. For purposes of this subsection, &quot;residential facility&quot; means any assisted living facility or residential facility in which aged, infirm or disabled persons reside with one or more resident counselors or other staff persons and for which the Department of Social Services is the licensing authority pursuant to this Code.</p><p>1990, c. 814, § 15.1-486.3; 1993, c. 373; 1997, c. 587; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0585" target="_blank" rel="noreferrer">585</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0601" target="_blank" rel="noreferrer">601</a>; 2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0840" target="_blank" rel="noreferrer">840</a>; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0796" target="_blank" rel="noreferrer">796</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0847" target="_blank" rel="noreferrer">847</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0476" target="_blank" rel="noreferrer">476</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0507" target="_blank" rel="noreferrer">507</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0238" target="_blank" rel="noreferrer">238</a>.</p>',4),n=[o];function s(l,c,d,f,h,g){return i(),r("div",null,n)}const m=e(t,[["render",s]]);export{u as __pageData,m as default};
