import{_ as t,o as n,c as o,z as e,a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 15.2-2293.1 Placement of amateur radio antennas.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2293.1.md","filePath":"vacode/15.2-2293.1.md"}'),r={name:"vacode/15.2-2293.1.md"},s=e("h1",{id:"§-15-2-2293-1-placement-of-amateur-radio-antennas",tabindex:"-1"},[a("§ 15.2-2293.1 Placement of amateur radio antennas. "),e("a",{class:"header-anchor",href:"#§-15-2-2293-1-placement-of-amateur-radio-antennas","aria-label":'Permalink to "§ 15.2-2293.1 Placement of amateur radio antennas."'},"​")],-1),i=e("p",null,"Any ordinance involving the placement, screening or height of antennas shall reasonably accommodate amateur radio antennas and shall impose the minimum regulation necessary to accomplish the locality's legitimate purpose. In localities having a population density of 120 persons or less per square mile according to the 1990 United States census, no local ordinance shall (i) restrict amateur radio antenna height to less than 200 feet above ground level as permitted by the Federal Communications Commission or (ii) restrict the number of support structures. In localities having a population density of more than 120 persons per square mile according to the 1990 United States census, no local ordinance shall (i) restrict amateur radio antenna height to less than 75 feet above ground level or (ii) restrict the number of support structures. Reasonable and customary engineering practices shall be followed in the erection of amateur radio antennas. This section shall not preclude any locality, by ordinance, from regulating amateur radio antennas with regard to reasonable requirements relating to the use of screening, setback, placement, and health and safety requirements.",-1),l=e("p",null,[a("1998, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0642",target:"_blank",rel:"noreferrer"},"642"),a(".")],-1),c=[s,i,l];fun