import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 15.2-2303.1:1 When certain cash proffers collected or accepted.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2303.1:1.md","filePath":"vacode/15.2-2303.1:1.md"}'),o={name:"vacode/15.2-2303.1:1.md"},n=t('<h1 id="§-15-2-2303-1-1-when-certain-cash-proffers-collected-or-accepted" tabindex="-1">§ 15.2-2303.1:1 When certain cash proffers collected or accepted. <a class="header-anchor" href="#§-15-2-2303-1-1-when-certain-cash-proffers-collected-or-accepted" aria-label="Permalink to &quot;§ 15.2-2303.1:1 When certain cash proffers collected or accepted.&quot;">​</a></h1><p>A. Notwithstanding the provisions of any cash proffer requested, offered, or accepted pursuant to § <a href="http://law.lis.virginia.gov/vacode/15.2-2298/" target="_blank" rel="noreferrer">15.2-2298</a>, <a href="http://law.lis.virginia.gov/vacode/15.2-2303/" target="_blank" rel="noreferrer">15.2-2303</a>, or <a href="http://law.lis.virginia.gov/vacode/15.2-2303.1/" target="_blank" rel="noreferrer">15.2-2303.1</a> for residential construction on a per-dwelling unit or per-home basis, cash payment made pursuant to such a cash proffer shall be collected or accepted by any locality only after completion of the final inspection and prior to the time of the issuance of any certificate of occupancy for the subject property.</p><p>B. Notwithstanding the provisions of any proffer to the contrary, the assertion of a right to delayed payment of cash proffers pursuant to this section shall not constitute cause for any action pursuant to § <a href="http://law.lis.virginia.gov/vacode/15.2-2299/" target="_blank" rel="noreferrer">15.2-2299</a>.</p><p>C. In addition to any other relief provided, the court may award reasonable attorney fees, expenses, and court costs to any person, group, or entity that prevails in an action successfully challenging an ordinance, administrative or other action as being in conflict with this section.</p><p>2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0549" target="_blank" rel="noreferrer">549</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0613" target="_blank" rel="noreferrer">613</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0173" target="_blank" rel="noreferrer">173</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0508" target="_blank" rel="noreferrer">508</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0798" target="_blank" rel="noreferrer">798</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0346" target="_blank" rel="noreferrer">346</a>.</p>',5),i=[n];function c(l,s,f,p,h,d){return r(),a("div",null,i)}const u=e(o,[["render",c]]);export{_ as __pageData,u as default};
